for f in `ls pwgs/counts/psub/`; do l=`wc -l pwgs/counts/psub/$f | awk '{print $1}'`; b=$(basename $f); for fb in `ls pwgs/bootstrap_counts/psub/${b%.phi.txt}/${b%.phi.txt}*mut_counts*`; do l2=`wc -l $fb | awk '{print $1}'`; if [ ! $l == $l2 ]; then echo $fb $l $l2; fi done ; done | wc -l

for f in `ls pwgs/counts/psub/`; do l=`wc -l pwgs/counts/psub/$f | awk '{print $1}'`; b=$(basename $f); for fb in `ls pwgs/bootstrap_counts/psub/${b%.phi.txt}/${b%.phi.txt}*mut_counts*`; do l2=`wc -l $fb | awk '{print $1}'`; if [ ! $l == $((2*$l2)) ]; then num=$(basename $fb); num=${num%.mut_counts.txt}; num=${num#${b%.phi.txt}}; num=${num#.}; fbm=/scratch/q/qmorris/yulia/pwgs/samples/psub/results_onlyKnownSignatures_psub/*/$b/mixtures.bootstrap_$num.csv;  echo $fbm; fi done ; done | wc -l
